<?php defined('BASEPATH') OR exit('No direct script access allowed');class Textos_model extends CI_Model {      public function __construct() {        parent::__construct();    }    public function lista_textos_geral() {        $this->db->select('*');        $this->db->from('tbl_textos');        $this->db->order_by('txt_id', "DESC");        return $this->db->get()->result_array();    }    public function lista_textos($id) {        $this->db->select('*');        $this->db->from('tbl_textos');        $this->db->where('txt_pagina', $id);        $this->db->order_by('txt_id', "DESC");        return $this->db->get()->result_array();    }    public function lista_texto($id) {        $this->db->select('*');        $this->db->from('tbl_textos');        $this->db->where('txt_id', $id);        return $this->db->get()->result_array();    }    public function adicionar($array) {        $this->db->insert('tbl_textos', $array);        return $this->db->insert_id();    }    public function excluir($id) {        $dados['txt_id'] = $id;        return $this->db->delete("tbl_textos", $dados);    }    public function editar($array) {        $this->db->set($array);        $this->db->where('txt_id', $array['txt_id']);        $this->db->update('tbl_textos');        return $this->db->affected_rows();    }}