<div class="content-wrapper">    <section class="content-header">        <h1>            Cadastro de Sessão            <small>Crie uma sessão do formulário</small>        </h1>    </section>    <section class="content">        <div class="box box-primary">            <div class="box-body">                <div class="row">                    <div class="col-md-12">                        <?= validation_errors('<div class="alert alert-danger">', '</div>'); ?>                        <form action="<?= base_url(''); ?>ocorrencia/cadastrar/<?= $id ?>" method="post"                              enctype="multipart/form-data">                            <?php foreach ($resposta as $res) { ?>                                <label><?= $res['cam_label'] ?></label>                                <?php                                if ($res['tip_codigo'] == 'text' || $res['tip_codigo'] == 'date' || $res['tip_codigo'] == 'email' ||                                    $res['tip_codigo'] == 'tel' || $res['tip_codigo'] == 'number') {                                    ?>                                    <input type="<?= $res['tip_codigo'] ?>" name="<?= $res['cam_name'] ?>"                                           id="<?= $res['cam_name'] ?>"                                           value="<?= $res['res_resposta'] ?>" <?= $res['cam_mandatory'] == true ? 'required' : NULL; ?>>                                <?php }                                if ($res['tip_codigo'] == 'select') {                                    ?>                                    <select class="form-control"                                    name="<?= $res['cam_name'] ?>"                                    id="<?= $res['cam_name'] ?>">                                    <option value="">Selecione</option>                                    <?php                                    foreach ($opcoes as $op) {                                        if ($op['opc_value'] == $res['res_resposta']) {                                            echo "<option value'" . $op['opc_value'] . "' checked>" . $op['opc_label'] . "</option>";                                        } else {                                            echo "<option value'" . $op['opc_value'] . "'>" . $op['opc_label'] . "</option>";                                        }                                    }                                    ?>                                </select>                                    <?php }                                    if($res['tip_codigo'] == 'radio' || $res['tip_codigo'] == 'checkbox'){                                    ?>                                        <div class="form-group">                                            <?php                                            foreach($opcoes as $op){                                                if($res['res_resposta'] == $op['opc_value'])                                                echo "<input type='".$res['tip_codigo']."' name='".$res['cam_name']."' id='".$res['cam_name']."' value='".$op['opc_value']."' checked>".$op['opc_label'];                                                else                                                    echo "<input type='".$res['tip_codigo']."' name='".$res['cam_name']."' id='".$res['cam_name']."' value='".$op['opc_value']."'>".$op['opc_label'];                                            }                                            ?>                                        </div>                                    <?php } ?>                            <?php } ?>                            <input class="btn btn-primary" type="submit" name="btn_enviar" id="btn_enviar"                                   value="Enviar"/>                        </form>                    </div>                </div>            </div>        </div></div>