       }        function aprovar(id) {            if(confirm("Deseja aprovar este formulário?")){            window.location.href = "<?= base_url('ocorrencia/status/1/');?>" + id;            }        }        function cancelar(id) {            if(confirm("Deseja cancelar este formulário?")){            window.location.href = "<?= base_url('ocorrencia/status/4/');?>" + id;            }        }        function finformacao(id) {            if(confirm("Deseja retornar este formulário?")){            window.location.href = "<?= base_url('ocorrencia/status/3/');?>" + id;            }        }    </script>    